import { Component, Input } from '@angular/core';
import * as Aos from 'aos';
import { Formulaire } from '../models/formulaire';
import { FormPortService } from '../form-port.service';

@Component({
  selector: 'app-education',
  templateUrl: './education.component.html',
  styleUrls: ['./education.component.css']
})
export class EducationComponent {


  @Input() personne: Formulaire[] = [];

  constructor(
    private formPort: FormPortService
  ) { }


  ngOnInit() {
  Aos.init();
    console.log(this.formPort.getFormulaire());
  }

}
<div class="section" id="education">
  <div class="container cc-education">
      <div class="h4 text-center mb-4 title">Education</div>
          <div class="card">
              <div class="row" *ngFor="let education of personne[0]?.education">
                  <div class="col-md-3 bg-primary" data-aos="fade-right" data-aos-offset="50" data-aos-duration="500">
                      <div class="card-body cc-education-header">
                          <p>{{education.startDate | date: 'dd/MM/yyyy'}} >>> {{education.endDate | date: 'dd/MM/yyyy'}}</p>
                      <div class="h5">Bachelor Degree</div>
                      </div>
                  </div>
                  <div class="col-md-9" data-aos="fade-left" data-aos-offset="50" data-aos-duration="500">
                      <div class="card-body">
                          <div class="h4">{{education?.title}}</div>
                              <h4 class="category">{{education.university}}</h4>
                              <h4 class="category">{{education.departement}}</h4>

                              <ul>
                                      <li>{{education.mention}} </li>
                              </ul>
                      </div>
                  </div>
              </div>
          </div>


  </div>
</div>
