  selector: 'app-affiche',
  templateUrl: './affiche.component.html',
  styleUrls: ['./affiche.component.css']
})
export class AfficheComponent {

personne : Formulaire[]=[];
constructor( private formServ:FormService) {

}

ngOnInit() {
  this.personne=this.formServ.getFormulaire();
  console.log(this.formServ.getFormulaire());
}



}
