import { Component } from '@angular/core';
import { FormControl, FormGroup, NgForm } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  loginForm: FormGroup = new FormGroup({
    //create a FormControl for each input
    name: new FormControl(''),
    password: new FormControl(''),
 });
 //handle the on submit
 onSubmit() {
    console.warn(this.loginForm.value);
    console.log('Name is ' + this.loginForm.value.name);
    console.log('Password is ' + this.loginForm.value.password);
 }

}
<!-- Register -->

<div class="container">
  <div class="row">
    <div class="col-md-6 mx-auto">
      <div class="card card-body">
        <h1 class="text-center mb-3"><i class="fas fa-sign-up-alt"></i>  Register</h1>
        <form [formGroup]="loginForm"  (ngSubmit)="onSubmit()">

          <div class="form-group">
            <label for="firstname">First name</label>
            <input type="text" id="firstname" name="firstname" class="form-control" formControlName="firstname" required>
          </div>
          <div class="form-group">
            <label for="lastname">Last name</label>
            <input type="text" id="lastname" name="lastname" class="form-control" formControlName="lastname" required>
          </div>
          <div class="form-group">
            <label for="phone">Phone</label>
            <input type="text" id="phone" name="phone" class="form-control" formControlName="phone" required>
          </div>
          <div class="form-group">
            <label for="address">Address</label>
            <input type="text" id="address" name="address" class="form-control" formControlName="address" required>
          </div>
          <div class="form-group">
            <label for="email">Email</label>
            <input type="email" id="email" name="email" class="form-control" formControlName="email" required>
          </div>
          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" name="password" class="form-control" formControlName="password" required>
          </div>



          <button type="submit" class="btn btn-primary btn-block"  >Register</button>
        </form>

      </div>
    </div>
  </div>
