import { Component } from '@angular/core';

@Component({
  selector: 'app-affiche',
  templateUrl: './affiche.component.html',
  styleUrls: ['./affiche.component.css']
})
export class AfficheComponent {



  cv: any = [
    {
      name: 'Mohamed',
      firstname: 'Ali',
      age: 25,
      cin: 12345678,
      job: 'student',
      path: 'assets/images/1.jpg'
    }]
;



  constructor() { }


ngOnInit(): void {
  console.log(this.cv);
}



}
<div class="card" style="width: 18rem;">
  <img class="card-img-top" src="{{cv.image}}" alt="Card image cap">
  <div class="card-body">
    <h5 class="card-title">{{cv.name}}</h5>
    <p class="card-text">{{cv.firstname}}</p>
    <p class="card-text">{{cv.age}}</p>
    <p class="card-text">{{cv.job}}</p>
    <p class="card-text">{{cv.path}}</p>
  </div>
{{cv.name}}
</div>
