import { Component, Input } from '@angular/core';
import { Formulaire } from '../models/formulaire';
import { FormPortService } from '../form-port.service';
import * as Aos from 'aos';

@Component({
  selector: 'app-skills',
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.css']
})
export class SkillsComponent {



  @Input() personne: Formulaire[] = [];

  constructor(
    private formPort: FormPortService
  ) { }

  ngOnInit() {
    Aos.init();
    }


}
<!-- skills  -->
<!-- Path: portfolio\src\app\skills\skills.component.html -->
<div class="container">
  <div class="h4 text-center mb-4 title">My Skills</div>
  <div class="card" data-aos="fade-up" data-aos-anchor-placement="top-bottom">
    <div class="card-body">
      <div class="row"  *ngFor="let skill of personne[0]?.skill">
        <div class="col-md-6" >
          <div class="progress-container progress-primary"><span class="progress-badge">{{  skill?.name }}</span>
            <div class="progress">
              <div class="progress-bar progress-bar-primary" data-aos="progress-full" data-aos-offset="10"
                data-aos-duration="2000" role="progressbar" aria-valuenow=80 aria-valuemin="0" aria-valuemax="100"
                style="width: 80%;"></div><span class="progress-value">{{  skill?.level }}</span>
            </div>
          </div>
        </div>

      </div>
    </div>
  </div>
</div>
