import { Component, Input } from '@angular/core';
import * as Aos from 'aos';
import { FormPortService } from '../form-port.service';
import { Formulaire } from '../models/formulaire';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent {


  @Input() personne: Formulaire[] = [];

  constructor(
    private formPort: FormPortService
  ) { }



  ngOnInit() {
    Aos.init();
    }

}
<!-- my project -->
<div class="section" id="projects">
  <div class="container">
      <div class="h4 text-center mb-4 title">My Projects</div>

      <div class="card">
          <div class="row"  *ngFor="let project of personne[0]?.project">
              <div class="col-md-3 bg-primary" data-aos="fade-right" data-aos-offset="50" data-aos-duration="500">
                  <div class="card-body cc-education-header">
                      <p>{{project.date | date: 'dd/MM/yyyy'}}</p>
                  <div class="h5"> {{project?.title}}</div>
                  </div>
              </div>
              <div class="col-md-9" data-aos="fade-left" data-aos-offset="50" data-aos-duration="500">
                  <div class="card-body">
                      <div class="h4">{{project?.title}}</div>
                          <h4 class="category">Project Description</h4>
                          <ul>
                              <li>P{{project.description}}</li>
                                  <li> {{project.link}}</li>

                          </ul>
                  </div>
              </div>
          </div>



      </div>
  </div>
</div>
<!-- my project -->
