import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {


  loginForm: FormGroup = new FormGroup({
    //create a FormControl for each input
    email: new FormControl(''),
    password: new FormControl(''),
 });
 loginObj: any = {
  email: '',
  password: ''
};

constructor(
  private router: Router,
) { }

ngOnInit(): void {
  const localData = localStorage.getItem('user');
  if (localData != null){
    this.loginObj = JSON.parse(localData);
  }
  }



onLogin() {
  console.log(this.loginObj);
  this.loginObj.email = this.loginForm.value.email;
  this.loginObj.password = this.loginForm.value.password;
  console.log(this.loginObj);
  localStorage.setItem('user', JSON.stringify(this.loginObj));
  alert('Login Successful');
  this.router.navigate(['/home']);
  this.loginForm
  // this.loginService.login(this.loginObj).subscribe((data) => {
  //   console.log(data);
  //   this.router.navigate(['/home']);
  // });
}
}
<!-- login -->

<div class="container">
  <div class="row">
    <div class="col-md-6 mx-auto">
      <div class="card card-body">
        <h1 class="text-center mb-3"><i class="fas fa-sign-in-alt"></i>  Login</h1>
        <form [formGroup]="loginForm"  (ngSubmit)="onLogin()">
          <div class="form-group">
            <label for="email">Email</label>
            <input type="email" id="email" name="email" class="form-control" formControlName="email" required>
          </div>
          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" name="password" class="form-control" formControlName="password" required>
          </div>
          <button type="submit" class="btn btn-primary btn-block">Login</button>
        </form>
        <p class="lead mt-4">
          No Account? <a routerLink="/register">Register</a>
        </p>
      </div>
    </div>
  </div>
