import { Injectable } from '@angular/core';
import { Formulaire } from './models/formulaire';

@Injectable({
  providedIn: 'root'
})
export class FormService {

private tab : Formulaire[] = [
  new Formulaire(1,'Mohamed','Bouhlel'),
  new Formulaire(2,'Mohamed','Bouhlel'),
  new Formulaire(3,'Mohamed','Bouhlel'),
];

  constructor() { }

  addFormulaire(newPersonne: Formulaire) {
    newPersonne._id = this.tab[this.tab.length - 1]._id + 1;
    this.tab.push(newPersonne);
  }

  getFormulaire() {
    return this.tab;
  }







}
